			assertEquals(scoreSummaryObject.getCashBeforeResolvedQuestion().get(resolvedQuestionID2), scoreSummaryObjectFilteredByQuestion.getCashBeforeResolvedQuestion().get(resolvedQuestionID2),PROB_ERROR_MARGIN);
			
